import ActionType from '../constants/actionTypes';
// import Config from '../constants/configs';

// Initial state of game
const initialState = {
    // history: [{
    //     x: null,
    //     y: null,
    //     squares: Array(Config.brdSize).fill(null).map(() => { return Array(Config.brdSize).fill(null)})
    // }],
    // nextMove: Config.xPlayer,
    // stepNumber: 0,
    // winCells: null,
    accendingMode: false
};

export default function handle(state = initialState, action) {
    switch (action.type) {
        case ActionType.CLICK:

            return state;

        case ActionType.CHANG_SORT: // Only change sort mode
            // eslint-disable-next-line no-alert
            // eslint-disable-next-line no-undef
            alert(state.accendingMode);
            return {
                accendingMode: state.accendingMode === true,
                ...state
            };

        case ActionType.JUMP_TO:

            return state;

        default:
            return state;
    }
}
